SELECT tbl_users.email AS email, tbl_users.name AS name,tbl_users_meta.meta_value AS ip_address,tbl_users_meta.meta_value AS referrer,tbl_users_meta.meta_value AS user_agent, tbl_users_images.url_path as path FROM tbl_users INNER JOIN tbl_users_images ON tbl_users_images.user_id = tbl_users.id INNER JOIN tbl_users_meta ON tbl_users_meta.user_id = tbl_users.id WHERE tbl_users_meta.meta_key='ip_address' OR tbl_users_meta.meta_key='referrer' OR tbl_users_meta.meta_key = 'user_agent' ORDER BY email;